mapper.select:
select pageid "rid", pagename "title", a.crtdate "dt", b.name "cataName", c.login_name "uname", case when d.report_id is null then 0 else 1 end as "iscollect" from portal_user_save a join portal_3greport_type b on a.cata_id = b.id join sc_login_user c on a.user_id = c.user_id left join (select * from report_collect where user_id = #{userId}) d on a.pageid = d.report_id where is3g = 'y'
and a.cata_id = #{cataId}
order by a.crtdate desc

select pageid, pagename, a.crtdate, a.updatedate, b.name "cataName", c.login_name from portal_user_save a, portal_3greport_type b, sc_login_user c where is3g = 'y' and a.cata_id = b.id and a.user_id = c.user_id
and a.cata_id = #{cataId}
order by a.crtdate desc
